    public int maxAreaOfIsland(int[][] grid) {
        if(grid.length ==0)
        {
            return 0;
        }
        int maxArea=0;
        boolean[][] visited = new boolean[grid.length][grid[0].length];
        for(int i=0;i<grid.length;i++)
        {
            for(int j=0;j<grid[i].length;j++)
            {
                int area = 0;
                if(grid[i][j] == 1)
                {
                    area = dfs(i,j, grid, 0, visited);
                }
                maxArea = Math.max(area, maxArea);
            }
        }

//        printGrid(grid);
        return maxArea;
    }
    public void printGrid(int[][] grid)
    {
        for(int i=0;i<grid.length;i++) {
            for (int j = 0; j < grid[i].length; j++) {
                System.out.print(grid[i][j]+", ");
            }
            System.out.println();
        }
    }
    public int dfs(int i, int j, int[][]grid, int currentCount, boolean[][] visited)
    {
        if(i>=0 && j>=0 && i< grid.length && j < grid[i].length && grid[i][j] > 0 && !visited[i][j])
        {
            visited[i][j] = true;

            currentCount++;
            currentCount = dfs(i-1,j, grid, currentCount, visited);
            currentCount = dfs(i+1,j, grid, currentCount, visited);
            currentCount = dfs(i,j-1, grid, currentCount, visited);
            currentCount = dfs(i,j+1, grid, currentCount, visited);



            return currentCount;
        }
        else {
            return currentCount;
        }

    }

